            return this.warn();
        }

        resolve() {
            return this.nodes.forEach(node => node.remove());
        }

        warn() {
            return this.nodes.forEach(node => {
                const { message, browsers, identifier, hack } = node._stylehacks;

                return node.warn(this.result, message, { browsers, identifier, hack });
